sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 '/usr/libexec/substrate';
sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 '/usr/libexec/substrated';
sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 'BasebandON';
