sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 'chflags nouchg /private/var/wireless/Library/Preferences/com.apple.commcenter.device_specific_nobackup.plist'
sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 'plutil -kPostponementTicket -ActivationTicket -remove /private/var/wireless/Library/Preferences/com.apple.commcenter.device_specific_nobackup.plist'
