sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 'chflags uchg /private/var/wireless/Library/Preferences/com.apple.commcenter.device_specific_nobackup.plist'
sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 'plutil -SetupDone -remove /var/mobile/Library/Preferences/com.apple.purplebuddy.plist';
sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 'plutil -SetupDone -true /var/mobile/Library/Preferences/com.apple.purplebuddy.plist';
sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 'plutil -SetupFinishedAllStepts -remove /var/mobile/Library/Preferences/com.apple.purplebuddy.plist';
sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 'plutil -SetupFinishedAllStepts -true /var/mobile/Library/Preferences/com.apple.purplebuddy.plist';
sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 'launchctl load /System/Library/LaunchDaemons';
sleep 12;
sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 'rm /Library/MobileSubstrate/DynamicLibraries/iuntethered.dylib';
sshpass -p 'alpine' ssh -o StrictHostKeyChecking=no root@localhost -p2222 'rm /Library/MobileSubstrate/DynamicLibraries/iuntethered.plist';
